import { AfterViewInit, Component } from '@angular/core';

@Component({
  selector: 'app-betslip',
  templateUrl: './betslip.component.html',
  styleUrls: ['./betslip.component.scss']
})
export class BetslipComponent implements AfterViewInit{

  @ViewChild('toggle') toggle: ElementRef | undefined;
  @ViewChild('.c-hamburger') toggles: any;

  ngAfterViewInit(): void {
    throw new Error('Method not implemented.');
  }


  toggleHandler() {
    this.toggles.forEach((toggle: any) => {
      toggle.nativeElement.addEventListener('click', (e: Event) => {
        e.preventDefault();
        if (toggle.nativeElement.classList.contains('is-active')) {
          toggle.nativeElement.classList.remove('is-active');
          // Si estás utilizando jQuery
          // $('.open').removeClass('oppenned');
        } else {
          toggle.nativeElement.classList.add('is-active');
          // Si estás utilizando jQuery
          // $('.open').addClass('oppenned');
        }
      });
    });
  }

}
