import { Component, ElementRef, OnInit, Renderer2 } from '@angular/core';
import { Router } from '@angular/router';
import { BetslipService } from '../service/betslip.service';

@Component({
  selector: 'app-betslip',
  templateUrl: './betslip.component.html',
  styleUrls: ['./betslip.component.css']
})
export class BetslipComponent implements OnInit {

  apuestas: { data: any, id: string }[] = [];
  constructor(
    private router: Router,
    private betslipService: BetslipService,
    private elRef: ElementRef,
    private renderer: Renderer2
  ) { }


  ngAfterViewInit() {
    const cardBet = this.elRef.nativeElement.querySelector('.cardBet');
    if (cardBet.children.length === 0) {
      const h2 = this.renderer.createElement('h2');
      const text = this.renderer.createText('No bets selected yet');
      this.renderer.appendChild(h2, text);
      this.renderer.appendChild(cardBet, h2);
    }
  }

  ngOnInit(): void {
    this.betslipService.obtenerApuestas().subscribe(apuestas => {
      this.apuestas = apuestas;
    });
  }

  irAHome() {
    this.router.navigate(['/home']);
  }
}
