import { Component, OnInit } from '@angular/core';
import { DataService } from '../service/data.service';
import { BetslipService } from '../service/betslip.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  bets: any = [];

  constructor(private dataService: DataService, private betslipService: BetslipService) { }

  ngOnInit(): void {
    this.dataService.getBets()
      .subscribe(data =>
        this.bets = data.filter(evento => evento.markets.length > 0));
  }

  toggleInfo(apuesta: any) {
    apuesta.showInfo = !apuesta.showInfo;
  }

  changeButtonColor(selection: any) {
    selection.clicked = !selection.clicked; // Invierte el estado del botón
    this.betslipService.agregarApuesta({ data: selection, id: selection.id });
  }

}
