import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class BetslipService {
  private apuestasSeleccionadas = new BehaviorSubject<{ data: any, id: string, clicked: boolean }[]>([]);

  agregarApuesta(apuesta: { data: any, id: string }) {
    const apuestas = this.apuestasSeleccionadas.value;
    const existingIndex = apuestas.findIndex(item => item.id === apuesta.id);

    if (existingIndex > -1) {
      // Si la apuesta ya existe, cambiar su estado de selección
      apuestas[existingIndex].clicked = !apuestas[existingIndex].clicked;
      apuestas.splice(existingIndex, 1);
    } else {
      // Si no existe, agregarla a la lista con el estado de selección como verdadero
      apuestas.push({ ...apuesta, clicked: true });
    }

    this.apuestasSeleccionadas.next(apuestas);
    console.log(apuestas);
  }

  obtenerApuestas() {
    return this.apuestasSeleccionadas.asObservable();
  }

  constructor() { }
}
