import { Component, OnInit } from '@angular/core';
import { DataService } from '../service/data.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  bets: any = [];

  constructor(private dataService: DataService) { }

  ngOnInit(): void {
    this.dataService.getBets()
      .subscribe(data =>
        this.bets = data.filter(evento => evento.markets.length > 0));
  }

  toggleInfo(apuesta: any) {
    apuesta.showInfo = !apuesta.showInfo;
  }

  buttonColor: { [key: string]: string } = {}; // Colores de los botones
  // Crea un objeto donde cada botón se identifica con un color específico

  changeColor(buttonId: string) {
    if (!this.buttonColor[buttonId] || this.buttonColor[buttonId] !== 'green') {
      this.buttonColor[buttonId] = 'green'; // Cambiar a verde si no está verde
    } else {
      this.buttonColor[buttonId] = ''; // Restaurar el color original
    }
  }
}
<div *ngFor="let apuesta of bets" class="apuesta-section">
    <h3 (click)="toggleInfo(apuesta)" class="apuesta">{{ apuesta.name }}</h3 >
    <div class="apuesta-info" *ngIf="apuesta.showInfo">
        <div *ngFor="let market of apuesta.markets" class="selectionBoxes">
            <h4>{{ market.name }}</h4>
            <ul class="list">
                <li *ngFor="let selection of market.selections">
                    <button (click)="changeColor(buttonId)" [ngStyle]="{'background-color': buttonColor[buttonId]}">{{ selection.name }} - {{ selection.price }}</button>
                </li>
            </ul>
        </div>
    </div>
</div>
